    "SHOW_CITIZEN_INFO_TO_ADMIN" BOOLEAN NOT NULL,
    "STATUS" ENUM('IN_PROGRESS', 'PENDING', 'REJECTED', 'REOPENED', 'RESOLVED') NOT NULL,
    "SUBMISSION_DATE" TIMESTAMP(6),
    "TITLE" CHARACTER VARYING(255) NOT NULL,
    "OFFICER_ID" BIGINT,
    "CITIZEN_ID" BIGINT
